import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Liste des catégories et sous-catégories
const categories = {
  "Généralisation": [
    "Modalité de nécessité",
    "Quantificateur universel",
    "Nominalisation"
  ],
  "Distorsion": [
    "Lecture de pensée",
    "Cause-effet",
    "Présupposés"
  ],
  "Omission": [
    "Comparatif non spécifié",
    "Verbe non spécifié",
    "Manque de référence"
  ]
};

function App() {
  // États pour la phrase et les nouveaux ajouts
  const [phrase, setPhrase] = useState(null);
  const [loading, setLoading] = useState(true);
  const [category, setCategory] = useState('');
  const [subCategory, setSubCategory] = useState('');

  // États pour soumettre une nouvelle phrase
  const [newText, setNewText] = useState('');
  const [newCategory, setNewCategory] = useState('');
  const [newSubCategory, setNewSubCategory] = useState('');

  // Fonction pour récupérer une phrase aléatoire avec filtres (si nécessaire)
  const fetchFilteredPhrase = async () => {
    try {
      setLoading(true);
      const params = {};
      if (category) params.category = category;
      if (subCategory) params.subCategory = subCategory;

      const response = await axios.get('http://localhost:8000/api/phrases', { params });
      setPhrase(response.data);
      setLoading(false);
    } catch (error) {
      console.error("Erreur lors de la récupération de la phrase :", error);
      setLoading(false);
    }
  };

  // Fonction pour soumettre une nouvelle phrase
  const submitNewPhrase = async () => {
    try {
      const newPhrase = {
        text: newText,
        category: newCategory,
        subCategory: newSubCategory,
      };
      await axios.post('http://localhost:8000/api/phrases', newPhrase);
      alert('Phrase ajoutée avec succès !');
      setNewText('');
      setNewCategory('');
      setNewSubCategory('');
    } catch (error) {
      console.error("Erreur lors de l'ajout de la phrase :", error);
    }
  };

  // Gérer le changement de catégorie et mettre à jour les sous-catégories
  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setNewCategory(selectedCategory);
    setNewSubCategory(''); // Réinitialiser la sous-catégorie lors du changement de catégorie
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Jeu du méta-modèle</h1>

      {/* Formulaire pour filtrer */}
      <div>
        <label>
          Filtrer par catégorie :
          <input
            type="text"
            value={category}
            onChange={e => setCategory(e.target.value)}
            placeholder="Ex: Généralisation"
          />
        </label>
        <label>
          Filtrer par sous-catégorie :
          <input
            type="text"
            value={subCategory}
            onChange={e => setSubCategory(e.target.value)}
            placeholder="Ex: Modalité de nécessité"
          />
        </label>
        <button onClick={fetchFilteredPhrase}>Appliquer les filtres</button>
      </div>

      {/* Si en cours de chargement */}
      {loading && <p>Chargement...</p>}

      {/* Si une phrase est disponible */}
      {phrase && (
        <div>
          <p><strong>Phrase :</strong> {phrase.text}</p>
          <p><strong>Catégorie :</strong> {phrase.category}</p>
          <p><strong>Sous-catégorie :</strong> {phrase.subCategory}</p>
        </div>
      )}

      {/* Formulaire pour ajouter une nouvelle phrase */}
      <h2>Ajouter une nouvelle phrase</h2>
      <input
        type="text"
        placeholder="Texte de la phrase"
        value={newText}
        onChange={(e) => setNewText(e.target.value)}
      />
      <br />
      <label>
        Catégorie :
        <select value={newCategory} onChange={handleCategoryChange}>
          <option value="">Sélectionner une catégorie</option>
          {Object.keys(categories).map((cat) => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </label>
      <br />
      <label>
        Sous-catégorie :
        <select value={newSubCategory} onChange={(e) => setNewSubCategory(e.target.value)}>
          <option value="">Sélectionner une sous-catégorie</option>
          {newCategory &&
            categories[newCategory].map((subCat) => (
              <option key={subCat} value={subCat}>{subCat}</option>
            ))}
        </select>
      </label>
      <br />
      <button onClick={submitNewPhrase}>Ajouter la phrase</button>
    </div>
  );
}

export default App;
