import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Importer le fichier CSS

// Liste des catégories et sous-catégories mise à jour
const categories = {
  "Omission": [
    "Suppression simple",
    "Manque du comparatif",
    "Verbe non-spécifique"
  ],
  "Généralisation": [
    "Quantifieurs universels",
    "Origine perdue",
    "Opérateurs modaux",
    "Nominalisation"
  ],
  "Distorsion": [
    "Lecture de pensée",
    "Faux cause-effet",
    "Équivalence complexe",
    "Présupposé"
  ]
};

function App() {
  // États pour la partie jeu
  const [phrase, setPhrase] = useState(null);
  const [categoryGuess, setCategoryGuess] = useState('');
  const [subCategoryGuess, setSubCategoryGuess] = useState('');
  const [correct, setCorrect] = useState(null); // true, false, or null
  const [loading, setLoading] = useState(true);

  // États pour le filtrage des phrases
  const [filterCategory, setFilterCategory] = useState('');
  const [filterSubCategory, setFilterSubCategory] = useState('');
  const [filteredPhrase, setFilteredPhrase] = useState(null);

  // États pour soumettre une nouvelle phrase
  const [newText, setNewText] = useState('');
  const [newCategory, setNewCategory] = useState('');
  const [newSubCategory, setNewSubCategory] = useState('');

  // Charger une phrase aléatoire pour la partie jeu
  const fetchRandomPhrase = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:8000/api/phrases');
      setPhrase(response.data);
      setLoading(false);
      setCorrect(null); // Réinitialiser le feedback
      setCategoryGuess('');
      setSubCategoryGuess('');
    } catch (error) {
      console.error("Erreur lors de la récupération de la phrase :", error);
      setLoading(false);
    }
  };

  // Vérifier si la catégorie et sous-catégorie devinées sont correctes
  const checkAnswer = () => {
    if (phrase) {
      const isCorrectCategory = phrase.category === categoryGuess;
      const isCorrectSubCategory = phrase.subCategory === subCategoryGuess;
      setCorrect(isCorrectCategory && isCorrectSubCategory);
    }
  };

  // Charger une phrase filtrée par catégorie et sous-catégorie
  const fetchFilteredPhrase = async () => {
    try {
      const params = {};
      if (filterCategory) params.category = filterCategory;
      if (filterSubCategory) params.subCategory = filterSubCategory;

      const response = await axios.get('http://localhost:8000/api/phrases', { params });
      setFilteredPhrase(response.data);
    } catch (error) {
      console.error("Erreur lors de la récupération de la phrase filtrée :", error);
    }
  };

  // Soumettre une nouvelle phrase
  const submitNewPhrase = async () => {
    try {
      const newPhrase = {
        text: newText,
        category: newCategory,
        subCategory: newSubCategory,
      };
      await axios.post('http://localhost:8000/api/phrases', newPhrase);
      alert('Phrase ajoutée avec succès !');
      setNewText('');
      setNewCategory('');
      setNewSubCategory('');
    } catch (error) {
      console.error("Erreur lors de l'ajout de la phrase :", error);
    }
  };

  // Charger une nouvelle phrase au démarrage pour la partie jeu
  useEffect(() => {
    fetchRandomPhrase();
  }, []);

  return (
    <div className="app-container">
      <h1>Jeu et gestion des phrases - Méta-modèle</h1>

      {/* Section 1: Jeu */}
      <div className="section">
        <h2>Jeu : Deviner la catégorie et la sous-catégorie</h2>

        {loading && <p>Chargement...</p>}
        {!loading && phrase && (
          <div className="phrase-container">
            <p><strong>Phrase :</strong> {phrase.text}</p>

            {/* Sélection de la catégorie */}
            <label>
              Sélectionner la catégorie :
              <select value={categoryGuess} onChange={(e) => setCategoryGuess(e.target.value)}>
                <option value="">Choisir une catégorie</option>
                {Object.keys(categories).map((cat) => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </label>

            {/* Sélection de la sous-catégorie */}
            <label>
              Sélectionner la sous-catégorie :
              <select
                value={subCategoryGuess}
                onChange={(e) => setSubCategoryGuess(e.target.value)}
                disabled={!categoryGuess} // Désactiver si la catégorie n'est pas sélectionnée
              >
                <option value="">Choisir une sous-catégorie</option>
                {categoryGuess &&
                  categories[categoryGuess].map((subCat) => (
                    <option key={subCat} value={subCat}>{subCat}</option>
                  ))}
              </select>
            </label>

            <button className="check-button" onClick={checkAnswer}>Vérifier la réponse</button>
          </div>
        )}

        {/* Affichage du résultat */}
        {correct === true && (
          <div className="result">
            <p className="correct">Correct ! 🎉</p>
            <p><strong>Question associée :</strong> {phrase.question}</p>
          </div>
        )}
        {correct === false && (
          <div className="result">
            <p className="incorrect">Incorrect 😞</p>
            <p><strong>La bonne catégorie :</strong> {phrase.category}</p>
            <p><strong>La bonne sous-catégorie :</strong> {phrase.subCategory}</p>
            <p><strong>Question à poser :</strong> {phrase.question}</p>
          </div>
        )}

        <button className="new-phrase-button" onClick={fetchRandomPhrase}>Nouvelle phrase</button>
      </div>

      {/* Section 2: Filtres */}
      <div className="section">
        <h2>Filtrer les phrases par catégorie et sous-catégorie</h2>

        <label>
          Catégorie :
          <select value={filterCategory} onChange={(e) => setFilterCategory(e.target.value)}>
            <option value="">Toutes les catégories</option>
            {Object.keys(categories).map((cat) => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </label>
        <label>
          Sous-catégorie :
          <select
            value={filterSubCategory}
            onChange={(e) => setFilterSubCategory(e.target.value)}
            disabled={!filterCategory}
          >
            <option value="">Toutes les sous-catégories</option>
            {filterCategory &&
              categories[filterCategory].map((subCat) => (
                <option key={subCat} value={subCat}>{subCat}</option>
              ))}
          </select>
        </label>
        <button className="filter-button" onClick={fetchFilteredPhrase}>Appliquer les filtres</button>

        {filteredPhrase && (
          <div className="phrase-container">
            <p><strong>Phrase filtrée :</strong> {filteredPhrase.text}</p>
            <p><strong>Catégorie :</strong> {filteredPhrase.category}</p>
            <p><strong>Sous-catégorie :</strong> {filteredPhrase.subCategory}</p>
            <p><strong>Question associée :</strong> {filteredPhrase.question}</p>
          </div>
        )}
      </div>

      {/* Section 3: Ajouter une nouvelle phrase */}
      <div className="section">
        <h2>Ajouter une nouvelle phrase</h2>
        <input
          type="text"
          placeholder="Texte de la phrase"
          value={newText}
          onChange={(e) => setNewText(e.target.value)}
        />
        <br />
        <label>
          Catégorie :
          <select value={newCategory} onChange={(e) => setNewCategory(e.target.value)}>
            <option value="">Sélectionner une catégorie</option>
            {Object.keys(categories).map((cat) => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Sous-catégorie :
          <select value={newSubCategory} onChange={(e) => setNewSubCategory(e.target.value)}>
            <option value="">Sélectionner une sous-catégorie</option>
            {newCategory &&
              categories[newCategory].map((subCat) => (
                <option key={subCat} value={subCat}>{subCat}</option>
              ))}
          </select>
        </label>
        <br />
        <button className="submit-button" onClick={submitNewPhrase}>Ajouter la phrase</button>
      </div>
    </div>
  );
}

export default App;
